CREATE TABLE "DEVOPS"."T_BUI_SQL_RULE"
( "ID" NUMBER NOT NULL,
"RULE_ID" VARCHAR2 ( 256 ) NOT NULL,
"RULE_DESC" VARCHAR2 ( 256 ) NOT NULL,
"RULE_TYPE" VARCHAR2 ( 1 ) NOT NULL,
"EFF_FLG" VARCHAR2 ( 1 ) NOT NULL,
"NOD_ID" VARCHAR2 ( 64 ) NOT NULL,
"TM_SMP" VARCHAR2 ( 26 ) NOT NULL,
"REQ_ID" VARCHAR2 ( 64 ) NOT NULL
);
ALTER TABLE "DEVOPS"."T_BUI_SQL_RULE" ADD CONSTRAINT "PK_T_BUI_SQL_RULE" PRIMARY KEY ( "ID" );
COMMENT ON TABLE "DEVOPS"."T_BUI_SQL_RULE" IS 'SQL审核规则配置表';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."ID" IS '主键';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."RULE_ID" IS '规则编号 每种规则编号固定';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."RULE_DESC" IS '规则描述';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."RULE_TYPE" IS '规则类型 1-DML 2-DDL create table 3-DDL alter table 4-sequence';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."EFF_FLG" IS '生效标志 Y生效 N失效';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."NOD_ID" IS '节点id';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."TM_SMP" IS '时间戳';
COMMENT ON COLUMN "DEVOPS"."T_BUI_SQL_RULE"."REQ_ID" IS '请求id';
